/*!

=========================================================
* BLK Design System PRO React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/blk-design-system-pro-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// nodejs library that concatenates classes
import classnames from "classnames";
// reactstrap components
import {
  Button,
  Card,
  CardBody,
  CardFooter,
  CardTitle,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Table,
  Container,
  Row,
  Col
} from "reactstrap";

// core components
import ColorNavbar from "components/Navbars/ColorNavbar.js";
import DemoFooter from "components/Footers/DemoFooter.js";

class BlogPosts extends React.Component {
  constructor(props) {
    super(props);
    let windowScrollTop;
    if (window.innerWidth >= 768) {
      windowScrollTop = window.pageYOffset / 3;
    } else {
      windowScrollTop = 0;
    }
    this.state = {
      transform: "translate3d(0," + windowScrollTop + "px,0)"
    };
  }
  resetTransform = () => {
    var windowScrollTop = window.pageYOffset / 3;
    this.setState({
      transform: "translate3d(0," + windowScrollTop + "px,0)"
    });
  };
  render() {
    return (
      <>
            <div>
              <Container>
                <Row>
                  <Col className="ml-auto mr-auto" md="10">
                    <br />
                    <Card className="card-blog card-plain blog-horizontal">
                      <Row>
                        <Col lg="4">
                          <div className="card-image">
                            <a href="#pablo" onClick={e => e.preventDefault()}>
                              <img
                                alt="..."
                                className="img rounded"
                                src={require("assets/img/trae-gould.jpg")}
                              />
                            </a>
                          </div>
                        </Col>
                        <Col lg="8">
                          <CardBody>
                            <CardTitle tag="h3">
                              <a
                                href="#pablo"
                                onClick={e => e.preventDefault()}
                              >
                                MateLabs mixes machine learning with IFTTT
                              </a>
                            </CardTitle>
                            <p className="card-description">
                              If you’ve ever wanted to train a machine learning
                              model and integrate it with IFTTT, you now can
                              with a new offering from MateLabs. MateVerse, a
                              platform where novices can spin out machine...If
                              you’ve ever wanted to train a machine learning
                              model and integrate it with IFTTT, you now can
                              with a new offering from MateLabs. MateVerse, a
                              platform where novices can spin out machine...{" "}
                              <a
                                href="#pablo"
                                onClick={e => e.preventDefault()}
                              >
                                Read More
                              </a>
                            </p>
                            <div className="author">
                              <img
                                alt="..."
                                className="avatar img-raised"
                                src={require("assets/img/james.jpg")}
                              />
                              <div className="text">
                                <span className="name">Tom Hanks</span>
                                <div className="meta">Drawn on 23 Jan</div>
                              </div>
                            </div>
                          </CardBody>
                        </Col>
                      </Row>
                    </Card>
                  </Col>
                </Row>
              </Container>
            </div>
      </>
    );
  }
}

export default BlogPosts;
