import React from "react";

// reactstrap components
import {
  Container,
  Row,
  Col,
} from "reactstrap";

// core components
import ColorNavbar from "components/Navbars/ColorNavbar.js";
import "./ProjectDescription.css";

function PictureFormat(props) {
  return(
    <div>
      <Col className="ml-auto mr-auto" md="8">
        <br/>
        <p className="project-image-title">{props.title}</p>
      </Col>
      <Row>
        <img
          alt="..."
          className="project-image"
          src={props.source}
        />
      </Row>
    </div>
  );
}


class StopMotion extends React.Component {
  componentDidMount() {
    document.documentElement.scrollTop = 0;
    document.scrollingElement.scrollTop = 0;
    this.refs.wrapper.scrollTop = 0;
    document.body.classList.add("stop-motion");
  }
  componentWillUnmount() {
    document.body.classList.remove("stop-motion");
  }
  render() {
    return (
      <>
        <ColorNavbar />
        <div className="wrapper" ref="wrapper">
          <div className="section">
            <Container>
              <Row className="space-70"/>
                <p className="project-type">Project</p>
                <br/>
                <p className="project-title">What Makes Us Human? A Stop Motion Animation Film.</p>
              <Row>
              <iframe 
                width="420" 
                height="315"
                src="https://www.youtube.com/watch?v=RdBhgXDCt7w&t=75s">
              </iframe>
              </Row>
              <Row>
                <Col className="ml-auto mr-auto" md="8">
                  <br/>
                  <h3 className="title">
                    Description
                  </h3>
                  <p>
                    Individuals want to donate to local charities, but struggle to integrate charitable giving into their everyday lives and unsure precisely how their donations impact the local community. Any solution has to be accessible, easy to use and free to users. 
                  </p>
                  <br/>
                </Col>
              </Row>
                <Row className="space-50"/>
                <PictureFormat
                  title="Home Page Navigation"
                  source={require("assets/img/giving-tree1.png")}
                />
                <Row className="space-50"/>
                <PictureFormat
                  title="Trivia Flow"
                  source={require("assets/img/giving-tree2.png")}
                />
                <Row className="space-50"/>
                <PictureFormat
                  title="Donation Flow"
                  source={require("assets/img/giving-tree3.png")}
                />
            </Container>
          </div>
        </div>
      </>
    );
  }
}

export default StopMotion;
